import React from 'react';
import { IconButton, Tooltip } from '@mui/material';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';

interface ProfileOrderButtonsProps {
  profileId: number;
}

const ProfileOrderButtons: React.FC<ProfileOrderButtonsProps> = ({ profileId }) => {
  const moveUp = () => {
    const token = localStorage.getItem('token');
    fetch(`/api/admin/profiles/${profileId}/moveUp`, {
      method: 'PATCH',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      if (response.ok) {
        window.location.reload();
      } else {
        console.error('Ошибка при перемещении профиля вверх');
      }
    })
    .catch(error => {
      console.error('Ошибка:', error);
    });
  };

  const moveDown = () => {
    const token = localStorage.getItem('token');
    fetch(`/api/admin/profiles/${profileId}/moveDown`, {
      method: 'PATCH',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      if (response.ok) {
        window.location.reload();
      } else {
        console.error('Ошибка при перемещении профиля вниз');
      }
    })
    .catch(error => {
      console.error('Ошибка:', error);
    });
  };

  return (
    <>
      <Tooltip title="Переместить выше">
        <IconButton onClick={moveUp} size="small" color="primary">
          <ArrowUpwardIcon />
        </IconButton>
      </Tooltip>
      <Tooltip title="Переместить ниже">
        <IconButton onClick={moveDown} size="small" color="primary">
          <ArrowDownwardIcon />
        </IconButton>
      </Tooltip>
    </>
  );
};

export default ProfileOrderButtons;
